action:

	if(layer[info_popup], removelayer(info_popup); );
	if(layer[info_popup_bg], removelayer(info_popup_bg); );


	addlayer(info_popup_bg);
	set(layer[info_popup_bg],
		type=container,
		align=lefttop,
		width=100%,
		height=100%,
		zorder=9999,
		bgcolor=0x000000,
		bgalpha=0,
		bgcapture=true,
		onclick=close_info_popup()
	);


	addlayer(info_popup);
	set(layer[info_popup],
		type=text,
		parent=info_popup_bg,
		align=center,
		edge=center,
		width=400,
		height=auto,
		bgcolor=0x1a1a1a,
		bgalpha=0.95,
		bgroundedge=12,
		bgborder=2 0x00adef 1.0,
		bgshadow=0 8 24 0x000000 0.8,
		padding=30,
		css=color:#ffffff; font-family:Arial; font-size:15px; line-height:1.6;,
		zorder=10000,
		alpha=0,
		scale=0.8
	);


	set(popup_content, '');
	txtadd(popup_content, '
');
	txtadd(popup_content, '
Mercedes-Benz');
	txtadd(popup_content, '
Découvrez l''excellence automobile allemande. Innovation, luxe et performance réunis dans chaque détail.');
	txtadd(popup_content, '
Notre gamme exclusive combine design raffiné et technologies de pointe pour une expérience de conduite incomparable.');
	txtadd(popup_content, '
');
	txtadd(popup_content, 'FERMER');
	txtadd(popup_content, '');
	txtadd(popup_content, '');

	copy(layer[info_popup].html, popup_content);


	tween(layer[info_popup_bg].bgalpha, 0.4, 0.3, easeOutQuad);
	tween(layer[info_popup].alpha, 1.0, 0.4, easeOutQuad);
	tween(layer[info_popup].scale, 1.0, 0.4, easeOutBack);

if(layer[info_popup],
	tween(layer[info_popup_bg].bgalpha, 0.0, 0.25, easeInQuad);
	tween(layer[info_popup].alpha, 0.0, 0.25, easeInQuad);
	tween(layer[info_popup].scale, 0.8, 0.25, easeInBack,
		removelayer(info_popup_bg);
		removelayer(info_popup);
	);
);
